import * as React from "react";
import { ReactSketchCanvas } from "react-sketch-canvas";

const styles = {
  border: "0.0625rem solid #9c9c9c",
  borderRadius: "0.25rem",
};

interface GameCanvasProps {
  // Define your component props here if needed
}

const GameCanvas = class extends React.Component {
  private canvas: React.RefObject<ReactSketchCanvas>;

  constructor(props: GameCanvasProps) {
    super(props);
    this.canvas = React.createRef<ReactSketchCanvas>();
  }

  render() {
    return (
      <div>
        <ReactSketchCanvas
          ref={this.canvas}
          strokeWidth={5}
          style={styles}
          strokeColor="black"
        />
        <button
          onClick={() => {
            this.canvas.current
              .exportSvg()
              .then((data) => {
                console.log(data);
              })
              .catch((e) => {
                console.log(e);
              });
          }}
        >
          Get Image
        </button>
      </div>
    );
  }
};

export default GameCanvas;
