import * as React from "react";
import { ReactSketchCanvas, ReactSketchCanvasRef } from "react-sketch-canvas";

const styles = {
  border: "0.0625rem solid #9c9c9c",
  borderRadius: "0.25rem",
};

interface GameCanvasProps {
  // GameCanvasProps (component props) defined here
}

interface GameCanvasState {
  // GameCanvasState (component states) defined here
}

const GameCanvas = class extends React.Component<
  GameCanvasProps,
  GameCanvasState
> {
  private canvas: React.RefObject<ReactSketchCanvasRef>;
  private canvasEnabled: boolean;

  constructor(props: GameCanvasProps) {
    super(props);
    this.canvas = React.createRef<ReactSketchCanvasRef>();
    this.canvasEnabled = true; // Enable the canvas initially
  }

  render() {
    return (
      <div>
        <ReactSketchCanvas
          ref={this.canvas}
          strokeWidth={5}
          style={styles}
          strokeColor="black"
          onStroke={console.log}
          allowOnlyPointerType={this.canvasEnabled ? "all" : "none"}
        />
        <button
          onClick={() => {
            if (this.canvas.current !== null)
              this.canvas.current
                .exportSvg()
                .then((data) => {
                  console.log(data);
                })
                .catch((e) => {
                  console.log(e);
                });
          }}
        >
          Get Image
        </button>
        <button
          onClick={() => {
            this.canvasEnabled = !this.canvasEnabled;
          }}
        >
          Get Image
        </button>
      </div>
    );
  }
};

export default GameCanvas;
